    user(id:  $userId) {
        id
        lastName
        firstName
    }
    profile(id: $profileId) {
        id
        sex
    }
    post(id:  $postId) {
        id
        content
    }
    memberType(data: {
        id: $memberTypeId
    }) {
        id
        discount
        monthPostsLimit
    }
}

